<div class="swiper-slide">  <div class="fcgs-gallery-item" >    <span class="fcgs-title-item fcgs-image-item">		<?php 			$sliderurl = get_post_meta( get_the_ID(),'rsris_fcgs_link', true );			$content = get_the_title();			$content = strip_tags($content);			$summary = $this->fcgs_truncate($content, 50);						if($sliderurl != '')			{		?>			<a href="<?php echo $sliderurl; ?>" title="<?php the_title(); ?>">				<?php					the_post_thumbnail( 'thumbnail');				?>
			</a>			<?php }else{				the_post_thumbnail( 'thumbnail');			} ?>    </span>    <span>	<?php 		if($sliderurl != '')		{	?>		<a class="fcgs-item-summary" href="<?php echo $sliderurl; ?>" title="<?php the_title(); ?>"><?php echo $summary; ?>		</a>		<?php }else{			echo $summary;		}		?>    </span>  </div></div>